//     }

const handleSubmit = () => {
        validateInput()
        // if (this.validateInput()) {
        //     this.uiData.updateShowLoading("flex")
        //     const postParams = {
        //         "userId": this.props.userId,
        //         "oldPassword": this.state.input.oldPassword,
        //         "newPassword": this.state.input.newPassword,
        //         "confirmPassword": this.state.input.confirmPassword
        //     }
        //     const errorLanguage: any = StaticKey.LOCALE_DATA.ERRORS
        //     const response = this.MyAccountService.updateChangePassword(postParams)
        //     response
        //         .then((value: any) => {
        //             this.uiData.updateShowLoading("none")
        //             console.log("val", value)
        //             if (!value.errorStatus) {
        //                 this.setState({
        //                     input: {
        //                         oldPassword: "",
        //                         newPassword: "",
        //                         confirmPassword: ""
        //                     }
        //                 })
        //                 this.uiData.setAlertShowMessage(
        //                     "show",
        //                     "",
        //                     StaticKey.LOCALE_DATA.MYACCOUNT.passwordChangeSuccess
        //                 )
        //             } else {
        //                 this.uiData.setAlertShowMessage("show", "", errorLanguage[value.errorCode])
        //             }
        //         })
        //         .catch((err: any) => {
        //             this.uiData.setAlertShowMessage(
        //                 "show",
        //                 "",
        //                 StaticKey.LOCALE_DATA.MYACCOUNT.somethingWentWrong
        //             )
        //         })
        // }
    }

    const validateInput = () => {
        const inputs = input
        const errors= {}
        let isValid = true

        //validation for old password
        if (!inputs["oldPassword"]) {
            isValid = false
            errors["oldPassword"] = 'please enter old password'
        }

        //validation for new password
        if (!inputs["newPassword"]) {
            isValid = false
            errors["newPassword"] = 'please enter new password'
        } else if (typeof inputs["newPassword"] !== "undefined") {
            const pattern = new RegExp( /^(?=.*[!@#$%*+=?&])(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])[A-Za-z\d!@#$%*+=?&|]{8,15}$/)
            if (!pattern.test(inputs["newPassword"])) {
                isValid = false
                errors["newPassword"] = 'password should contain one uppercase , special character and numerics'
            }
        } else if (
            inputs.confirmPassword &&
            inputs.newPassword !== inputs.confirmPassword
        ) {
            isValid = false
            errors["confirmPassword"] = 'please enter same value'
        } else {
            isValid = false
            errors["confirmPassword"] = inputs.confirmPassword
                ? ""
                : errors.confirmPassword
        }

        //validation for confirm password
        if (!inputs["confirmPassword"]) {
            isValid = false
            errors["confirmPassword"] = 'please enter confirm password'
        } else if (
            inputs.newPassword &&
            inputs.confirmPassword !== inputs.newPassword
        ) {
            isValid = false
            errors["confirmPassword"] = 'please enter same value'
        }

        setError({ errors})
        return isValid
    }


  return (
    <>
      <div className="row">
        <div className="col-12 col-lg-6 offset-lg-3">
            <form>
                <div className='form-grounp'>
                    <input
                        type="password"
                        autoComplete="off"
                        className= "form-control"
                        placeholder='enter old password'
                        value={input.oldPassword}
                        name="oldPassword"
                        id="formControlInput1"
                        autoFocus={true}
                        onChange=''/>
                    <label 
                        className="error"
                        htmlFor="formControlInput1">
                    {error.oldPassword}
                    </label>
                </div>
                <div className='form-grounp'>
                    <input
                        type="password"
                        autoComplete="off"
                        className= "form-control"
                        placeholder='enter new password'
                        value={input.newPassword}
                        name="newPassword"
                        id="formControlInput2"
                        onChange=''/>
                    <label 
                        className="error"
                        htmlFor="formControlInput2">
                        {error.newPassword}
                    </label>
                </div>
                <div className='form-grounp'>
                    <input
                        type="password"
                        autoComplete="off"
                        className= "form-control"
                        placeholder='enter confirm password'
                        value={input.confirmPassword}
                        name="confirmPassword"
                        id="formControlInput3"
                        autoFocus={true}
                        onChange=''/>
                    <label 
                        className="error"
                        htmlFor="formControlInput3">
                            {error.confirmPassword}
                    </label>
                </div>
                <div>
                    <button
                        className="submit-button rounded-pill check-btn hvr-sweep-to-right update-btn-change"
                        onClick={handleSubmit()}>
                                    Submit
                    </button>
                </div>
            </form>
        </div>
      </div>
   </>
  )
}

export default ChangePassword
